import { createStore } from "vuex";
import { auth } from "../firebaseConfig/firebaseConfig.js";

export default createStore({
  state: { currentAuth: false },

  mutations: {
    setCurrentAuth(state, auth) {
      state.currentAuth = auth;
    },
    logout(state) {
      state.currentAuth = null;
    },

    verifyAuth() {
      let self = this;
      auth.onAuthStateChanged(async function (user) {
        if (user != null) {
          self.email = user.email;
          self.authentication = true;
          self.id = user.uid;
          self.carritoNumero = await self.contarProd(user.uid);
        } else {
          self.authentication = false;
        }
      });
    },
    async contarProd(id) {
      let cantidad = 0;
      const data = await fetch(`http://localhost:3003/v1/api/carts/${id}`).then(
        (res) => res.json()
      );
      for (let i = 0; i <= data.cesta.length - 1; i++) {
        cantidad += parseInt(data.cesta[i].cantidad);
      }
      return cantidad;
    },
  },
});
