import { createStore } from "vuex";

export default createStore({
  state: { currentAuth: false },

  mutations: {
    setCurrentAuth(state, auth) {
      state.currentAuth = auth;
    },
    logout(state) {
      state.currentAuth = null;
    },
  },
});
